import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { withFirebase } from '../Firebase';
import { PDFDownloadLink, Text, Document, Page } from '@react-pdf/renderer'
import ReactPDF from '@react-pdf/renderer';
import './index.css';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas'

const pdf = () => (

  <div id="signupform">
  <center>
      <h1>My Hospital</h1>
      <div className="row">
        <div className="col4">
          Doctor Name: XYZ
        </div><br/>
        <div className="col4">
          License No.: 123456
        </div>
        <div className="col4">
          Degree: MBBS
        </div>
      </div>
    </center>
    <hr />
    <PdfPage />    
    <hr />
        <center>
          <div className="row">
            <div className="col6">
              Contact No.: 123456
            </div>
            <div className="col6">
              Timing: 10:00am-11:00pm
            </div>
          </div>
        </center>
        <div>
    
  </div>
  </div>
);

const myDoc = () => (
  <div id="signupform">
  <pdf />
  
  </div>
);

const INITIAL_STATE = {
  username: '',
  age: 0,
  symptoms: '',
  diagnosis: '',
  prescription: '',
  suggestions: '',
  advice: '',
  error: null,
};

class pdfBase extends Component {
  constructor(props) {
    super(props);
	  
	this.state = { ...INITIAL_STATE };
  }

  onSubmit = event => {
    const { username, age, symptoms, diagnosis, prescription,
            suggestions, advice } = this.state;

    // const reactpdf = () => (
    //   <Document>
    //   <Page size="A4" >
    //     {/* <pdf /> */}
    //     <Text>Section #1</Text>
    //   </Page>
    //   </Document>
    // );
           
    // ReactPDF.render(<reactpdf />);
    // console.log("print!!!!!!");

    const input = document.getElementById('signupform');
    html2canvas(input)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/jpeg', 1.0);
        const pdf = new jsPDF("l", "mm", "a4");
        const imgProps= pdf.getImageProperties(imgData);
        const pdfWidth = pdf.internal.pageSize.getWidth();
        pdf.addImage(imgData, 'JPEG', 0, 0);
        // pdf.output('dataurlnewwindow');
        pdf.save("download.pdf");
      })
    ;
      
    
    event.preventDefault();
  }
  
  onChange = event => {
	  this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    const { 
      username, 
      age, 
      symptoms,
      diagnosis,
      prescription,
      suggestions,
      advice,
      error,
     } = this.state;

     console.log(this.props.location.PatientName);
     
     const isInvalid =     
     prescription === '' ||
     username === '';
  
    return (
      
      <form onSubmit={this.onSubmit}>
        <div className="input-group">
        <div className="form-group">
                    <label>Patient Name</label>
	      <input
          name="username" className="form-control"
          value={this.props.location.state.PatientName}
          onChange={this.onChange}
          type="text"
          placeholder="patient name"
        /></div>

        <div className="input-group-prepend">
        <span></span>
        </div>
        
        <div className="form-group ">
                    <label>Age</label>
        <input
          name="age" className="form-control"
          value={this.props.location.state.Age}
          onChange={this.onChange}
          type="number"
          placeholder="age"
        /></div>
        </div>

        
        
        <div className="form-group ">
                    <label>Symptoms</label>
        <textarea
          name="symptoms" className="form-control"
          value={this.props.location.state.Symptoms}
          onChange={this.onChange}
          type="text"
          placeholder="symptoms"
        /></div>
        <div className="form-group ">
                    <label>Diagnosis</label>
        <textarea
          name="diagnosis" className="form-control"
          value={this.props.location.state.Diagnosis}
          onChange={this.onChange}
          type="text"
          placeholder="diagnosis"
        /></div>
        <div className="form-group ">
                    <label>prescription</label>
        <textarea
          name="prescription" className="form-control"
          value={this.props.location.state.Medicines}
          onChange={this.onChange}
          type="text"
          placeholder="prescription"
        /></div>
        <div className="form-group ">
                    <label>suggestions</label>
        <textarea
          name="suggestions" className="form-control"
          value={this.props.location.state.Recommendations}
          onChange={this.onChange}
          type="text"
          placeholder="suggestions"
        /></div>
        <div className="form-group ">
                    <label>Other advices</label>
        <textarea
          name="advice" className="form-control"
          value={this.props.location.state.Advice}
          onChange={this.onChange}
          type="text"
          placeholder="advices"
        /></div>       

        <br/>
        <button className="btn btn-primary btn-block" type="submit">
          Confirm and Generate
        </button><br/>
        {error && <p>{error.message}</p>}
	  
      </form>
    );
  }
}


const PdfPage =withRouter(withFirebase(pdfBase));

export default pdf;

export { PdfPage };
