import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { withFirebase } from '../Firebase';
import firebase from 'firebase';

import './index.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import * as ROUTES from '../../constants/routes';
import ReactSearchBox from 'react-search-box'

class scheduling extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      users: [],
      db: firebase.firestore()
    };
    firebase.firestore().settings({ timestampsInSnapshots: true});

    // let user = firebase.firestore().collection('Patient_Details').get()
    // .then(querySnapshot => {
    //   querySnapshot.docs.map(doc => {
    //     console.log('LOG 1', doc.data());
    //     let user = doc.data();
    //     return user;
    //   });
    // });
  }

   async getData() {
     let tempDoc = [];
    await this.props.firebase.db.collection('Patient_Details').get().then((querySnapshot) => {
     // const tempDoc = []
      querySnapshot.forEach((doc) => {
         tempDoc.push({ key: doc.id, value:doc.data().name, ...doc.data() })
      })
      console.log(tempDoc);
      this.setState({
        users: tempDoc,
        loading: false,
      });          
   });
   return tempDoc;
  }

  async componentDidMount() {
    this.setState({ loading: true });
    
    await this.props.firebase.db.collection('Patient_Details').get().then((querySnapshot) => {
      const tempDoc = []
      querySnapshot.forEach((doc) => {
         tempDoc.push({ key: doc.id, value:doc.data().name, ...doc.data() })
      })
      console.log(tempDoc);
      this.setState({
        users: tempDoc,
        loading: false,
      });  
        
   });
	  
  }
  
  componentWillUnmount() {
   // firebase.firestore().collection('patients');
  }
  
  render() {
    const { users, loading } = this.state;
    //const u = this.getData();
    console.log(users);
    return (
      <div>
        {!loading && 
  <div class="wrap">
   <div class="search">
     <center><h3>Search a patient</h3></center>
   <ReactSearchBox 
      placeholder="Enter patient name"
      data={users}
      onSelect={event =>  window.location.href=ROUTES.SPEECH}
      onFocus={() => {
        //console.log(users);
        //console.log('This function is called when is focussed')
      }}
      onChange={name=>{}}
      fuseConfigs={{
        threshold: 0.05,
      }}
      value=" "
    />
</div>
 <br/>
    <h4>Or</h4>
    <br/>
    
    <h3> <Link to={ROUTES.PATIENT_REG}>Register new patient</Link></h3>
  </div>
  }
  <UserList users={users} />    
  </div>


);
  }
}

const UserList = ({ users }) => (
  <ul>
    {users.length>0 && users.map(user => (
      <li key={user.key}>
        <span>
          <strong>Email:</strong> {user.email}
        </span>
        
        <span>
          <strong>Name:</strong> {user.name}
        </span>
      </li>
    ))}
  </ul>
);

export default withFirebase(scheduling);
