import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { withFirebase } from '../Firebase';
import * as ROUTES from '../../constants/routes';
const pdfPage = () => (
  <form id="pdfPage">
  <div>
  <center>
      <h1>My Hospital</h1>
      <div class="row">
        <div class="col4">
          Doctor Name: XYZ
        </div>
        <div class="col4">
          License No.: 123456
        </div>
        <div class="col4">
          Degree: MBBS
        </div>
      </div>
    </center>
    <hr />
    <pdfFormPage />
    
    <hr />
        <center>
          <div class="row">
            <div class="col6">
              Contact No.: 123456
            </div>
            <div class="col6">
              Timing: 10:00am-11:00pm
            </div>
          </div>
        </center>
  </div>
  </form>
);

const INITIAL_STATE = {
  username: '',
  age: 0,
  symptoms: '',
  diagnosis: '',
  prescription: '',
  suggestions: '',
  advice: '',
  error: null,
};

class pdfFormPage extends Component {
  constructor(props) {
    super(props);
	
	this.state = { ...INITIAL_STATE };
  }

  onSubmit = event => {
    const { username, age, symptoms, diagnosis, prescription,
            suggestions, advice } = this.state;
    this.props.firebase
      .doCreateUserWithEmailAndPassword(email, passwordOne)
      .then(authUser => {
        // Create a user in your Firebase realtime database
        return this.props.firebase
          .user(authUser.user.uid)
          .set({
            username,
            email,
          });
      })
      .then(() => {
        this.setState({ ...INITIAL_STATE });
        this.props.history.push(ROUTES.HOME);
      })
      .catch(error => {
        this.setState({ error });
      });
    event.preventDefault();
  }
  
  onChange = event => {
	  this.setState({ [event.target.name]: event.target.value });
  };
  render() {
    const { username, age, symptoms, diagnosis, prescription,
      suggestions, advice } = this.state;
	
  
    return (
      <form onSubmit={this.onSubmit}>
        <div className="form-group ">
                    <label>Patient Name</label>
	  <input
          name="username" className="form-control"
          value={username}
          onChange={this.onChange}
          type="text"
          placeholder="patient name"
        /></div>
        <div className="form-group ">
                    <label>Age</label>
        <input
          name="age" className="form-control"
          value={email}
          onChange={this.onChange}
          type="number"
          placeholder="age"
        /></div>
        <div className="form-group ">
                    <label>Symptoms</label>
        <input
          name="symptoms" className="form-control"
          value={diagnosis}
          onChange={this.onChange}
          type="text"
          placeholder="symptoms"
        /></div>
        <div className="form-group ">
                    <label>Diagnosis</label>
        <input
          name="diagnosis" className="form-control"
          value={diagnosis}
          onChange={this.onChange}
          type="text"
          placeholder="diagnosis"
        /></div>
        <div className="form-group ">
                    <label>prescription</label>
        <input
          name="prescription" className="form-control"
          value={prescription}
          onChange={this.onChange}
          type="text"
          placeholder="prescription"
        /></div>
        <div className="form-group ">
                    <label>suggestions</label>
        <input
          name="suggestions" className="form-control"
          value={suggestions}
          onChange={this.onChange}
          type="text"
          placeholder="suggestions"
        /></div>
        <div className="form-group ">
                    <label>Other advices</label>
        <input
          name="advices" className="form-control"
          value={advices}
          onChange={this.onChange}
          type="text"
          placeholder="advices"
        /></div>       

        <br/>
        <button disabled={isInvalid}  className="btn btn-primary btn-block" type="submit">
          Confirm and Generate
        </button><br/>
        {error && <p>{error.message}</p>}
	  
      </form>
    );
  }
}


const pdfForm =withRouter(withFirebase(pdfForm));

export default pdfFormPage;

export { pdfForm };
