import React, { Component } from 'react';
import * as ROUTES from '../../constants/routes';
import './index.css';
const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition
const recognition = new SpeechRecognition()

recognition.continous = true
recognition.interimResults = true
recognition.lang = 'en-US'


class Speech extends Component {

  constructor(props) {
    super(props);
    this.state = {
      listening: false
    }
    this.toggleListen = this.toggleListen.bind(this)
    this.handleListen = this.handleListen.bind(this)
  }
  
  onSubmit = event => {
    fetch('http://localhost:9000/name?data=' + document.getElementById('finaltext').value)
    .then(res => res.json())
    .then(res => this.setState({apiResponse : res}));

    this.props.history.push({
      pathname: ROUTES.PRESCRIPTION,
      data: this.apiResponse, // your data array of objects
    }) 
    
    event.preventDefault();
  }

  toggleListen() {
    this.state.listening = !this.state.listening;
     this.handleListen();
  }
  
  handleListen(){ 
    if (this.state.listening) {
      document.getElementById('microphone-btn').value = "stop";
      recognition.start()
    } else {
      document.getElementById('microphone-btn').value = "record";
      recognition.stop()      
    }

    let finalTranscript = ''
    recognition.onresult = event => {
      let interimTranscript = ''

      for (let i = event.resultIndex; i < event.results.length; i++) {
        const transcript = event.results[i][0].transcript;
        if (event.results[i].isFinal) finalTranscript += transcript + ' ';
        else interimTranscript += transcript;
      }
      if(interimTranscript.length<=1){
        document.getElementById('interim').innerHTML = "!"  
      }else{
        document.getElementById('interim').innerHTML = interimTranscript
      }

      document.getElementById('finaltext').value = finalTranscript


  }
}

  render() {
    return (
      <div className="wrap">
        <form onSubmit={this.onSubmit}>
        <button id="microphone-btn" className="btn btn-primary btn-block btn-lg" onClick={this.toggleListen}>Record</button><br/>
        <button id="recognize-btn" className="btn btn-primary btn-block btn-lg" type="submit">Recognize</button>
        <div id="interim" color='gray'></div>
        <textarea id="finaltext" className="form-control" placeholder="transcript" color='black'/>
        </form>
      </div>
    )
  }
}

export default Speech;
