import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { withFirebase } from '../Firebase';
import './index.css';

const pdf = () => (
  <form id="signupform">
   <div>
  <center>
      <h1>My Hospital</h1>
      <div class="row">
        <div class="col4">
          Doctor Name: XYZ
        </div><br/>
        <div class="col4">
          License No.: 123456
        </div>
        <div class="col4">
          Degree: MBBS
        </div>
      </div>
    </center>
    <hr />
    <PdfPage />    
    <hr />
        <center>
          <div class="row">
            <div class="col6">
              Contact No.: 123456
            </div>
            <div class="col6">
              Timing: 10:00am-11:00pm
            </div>
          </div>
        </center>
  </div>
  </form>
  
);

const INITIAL_STATE = {
  username: '',
  age: 0,
  symptoms: '',
  diagnosis: '',
  prescription: '',
  suggestions: '',
  advice: '',
  error: null,
};

class pdfBase extends Component {
  constructor(props) {
    super(props);
	  
	this.state = { ...INITIAL_STATE };
  }

  onSubmit = event => {
    const { username, age, symptoms, diagnosis, prescription,
            suggestions, advice } = this.state;
           
       
    
    event.preventDefault();
  }
  
  onChange = event => {
	  this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    const { 
      username, 
      age, 
      symptoms,
      diagnosis,
      prescription,
      suggestions,
      advice,
      error,
     } = this.state;

     console.log(this.props.location.PatientName);
     
     const isInvalid =     
     prescription === '' ||
     username === '';
  
    return (
      
      <form onSubmit={this.onSubmit}>
        <div class="input-group">
        <div className="form-group row">
                    <label>Patient Name</label>
	      <input
          name="username" className="form-control"
          value={this.props.location.data.PatientName}
          onChange={this.onChange}
          type="text"
          placeholder="patient name"
        /></div>

        <div class="input-group-prepend">
        <span></span>
        </div>
        
        <div className="form-group ">
                    <label>Age</label>
        <input
          name="age" className="form-control"
          value={this.props.location.Age}
          onChange={this.onChange}
          type="number"
          placeholder="age"
        /></div>
        </div>

        
        
        <div className="form-group ">
                    <label>Symptoms</label>
        <textarea
          name="symptoms" className="form-control"
          value={this.props.location.Symptom}
          onChange={this.onChange}
          type="text"
          placeholder="symptoms"
        /></div>
        <div className="form-group ">
                    <label>Diagnosis</label>
        <textarea
          name="diagnosis" className="form-control"
          value={this.props.location.Diagnosis}
          onChange={this.onChange}
          type="text"
          placeholder="diagnosis"
        /></div>
        <div className="form-group ">
                    <label>prescription</label>
        <textarea
          name="prescription" className="form-control"
          value={this.props.location.Medicines}
          onChange={this.onChange}
          type="text"
          placeholder="prescription"
        /></div>
        <div className="form-group ">
                    <label>suggestions</label>
        <textarea
          name="suggestions" className="form-control"
          value={this.props.location.Recommendations}
          onChange={this.onChange}
          type="text"
          placeholder="suggestions"
        /></div>
        <div className="form-group ">
                    <label>Other advices</label>
        <textarea
          name="advice" className="form-control"
          value={this.props.location.Advice}
          onChange={this.onChange}
          type="text"
          placeholder="advices"
        /></div>       

        <br/>
        <button disabled={isInvalid}  className="btn btn-primary btn-block" type="submit">
          Confirm and Generate
        </button><br/>
        {error && <p>{error.message}</p>}
	  
      </form>
    );
  }
}


const PdfPage =withRouter(withFirebase(pdfBase));

export default pdf;

export { PdfPage };
