import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import { withFirebase } from '../Firebase';
import { PDFDownloadLink, Text, Document, Page } from '@react-pdf/renderer'
import ReactPDF from '@react-pdf/renderer';
import './index.css';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas'

const pdf = () => (

  <div id="pdfform">
    <PdfPage />    
  </div>

);

const myDoc = () => (
  <div id="signupform">
  <pdf />
  
  </div>
);

const INITIAL_STATE = {
  hospital: '',
  doctorname: '',
  phonenumber: '',
  degree: '',
  lic_no: '',

  username: '',
  age: 0,
  symptoms: '',
  diagnosis: '',
  prescription: '',
  suggestions: '',
  advice: '',
  error: null,
};

class pdfBase extends Component {
  constructor(props) {
    super(props);   


	this.state = { username: this.props.location.state.PatientName,
  age: this.props.location.state.Age,
  symptoms: this.props.location.state.Symptoms,
  diagnosis: this.props.location.state.Diagnosis,
  prescription: this.props.location.state.Medicines,
  suggestions: this.props.location.state.Recommendations,
  advice: this.props.location.state.Advice,
  error: null, };

//get data form firestore
const db = this.props.firebase.db;
db.settings({
  timestampsInSnapshots: true
});
db.collection("Doctors")
.doc(this.props.firebase.auth.currentUser.uid)
.get()
.then(doc => {
  const data = doc.data();
  console.log(data);
  console.log(data.hospital);
  document.getElementById('hospital').innerHTML = data.hospital;
  document.getElementById('degree').innerHTML = "Degree: " + data.degree;
  document.getElementById('doctorname').innerHTML = "Doctor Name: " + data.fullname;
  document.getElementById('phonenumber').innerHTML = "Contact: " + data.phonenumber;
  document.getElementById('lic_no').innerHTML = "Licence no: " + data.lic_no; 
  
});
  
  }
  

  onSubmit = event => {
    const { username, age, symptoms, diagnosis, prescription,
            suggestions, advice } = this.state;

    document.getElementById("btn").style.visibility="hidden";
    document.getElementById('sugg_txt').innerHTML = document.getElementById('sugg_txt').value;


    const input = document.getElementById('pdfform');
    html2canvas(input)
      .then((canvas) => {
        const imgData = canvas.toDataURL('image/jpeg', 1.0);
        const ctx = canvas.getContext("2d");
        ctx.scale(0.8, 0.8);
        const pdf = new jsPDF("p", "mm", "a4");
        const imgProps= pdf.getImageProperties(imgData);
        pdf.addImage(imgData, 'JPEG', 10, 10, 190, 230);
        // pdf.output('dataurlnewwindow');
        pdf.save("download.pdf");
      })
    ;
      
    document.getElementById("btn").style.visibility="visible";
    
    event.preventDefault();
  }
  
  onChange = event => {
	  this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    const { 
      username, 
      age, 
      symptoms,
      diagnosis,
      prescription,
      suggestions,
      advice,
      error,
     } = this.state;

         
     const isInvalid =     
     prescription === '' ||
     username === '';
  
    return (

      <div>
      <center>
      <h1 id="hospital">My Hospital</h1>
      <div className="row">
        <div id="doctorname" className="col4">
          Doctor Name: XYZ
        </div><br/>
        <div id="lic_no" className="col4">
          License No.: 123456
        </div>
        <div id="degree" className="col4">
          Degree: MBBS
        </div>
      </div>
      
  </center>
           <hr /> 
      <form onSubmit={this.onSubmit}>
        <div className="input-group">
        <div className="form-group">
                    <label>Patient Name</label>
	      <input
          name="username" className="form-control"
          value={username}
          onChange={this.onChange}
          type="text"
          placeholder="patient name"
        /></div>

        <div className="input-group-prepend">
        <span></span>
        </div>
        
        <div className="form-group ">
                    <label>Age</label>
        <input
          name="age" className="form-control"
          value={age}
          onChange={this.onChange}
          type="number"
          placeholder="age"
        /></div>
        </div>
       
        <div className="form-group ">
                    <label>Symptoms</label>
        <textarea
          name="symptoms" className="form-control"
          value={symptoms}
          onChange={this.onChange}
          type="text"
          placeholder="symptoms"
        /></div>
        <div className="form-group ">
                    <label>Diagnosis</label>
        <textarea
          name="diagnosis" className="form-control"
          value={diagnosis}
          onChange={this.onChange}
          type="text"
          placeholder="diagnosis"
        /></div>
        <div className="form-group ">
                    <label>prescription</label>
        <textarea
          name="prescription" className="form-control"
          value={prescription}
          onChange={this.onChange}
          type="text"
          rows="4"
          placeholder="prescription"
        /></div>
        <div className="form-group ">
                    <label>suggestions</label>
        <textarea
          name="suggestions" className="form-control"
          value={suggestions} id="sugg_txt"
          onChange={this.onChange}
          type="text"
          rows="4"
          placeholder="suggestions"
        /></div>
        <div className="form-group ">
                    <label>Other advices</label>
        <textarea
          name="advice" className="form-control"
          value={advice}
          onChange={this.onChange}
          type="text"          
          placeholder="advices"
        /></div>       

        <br/>
        <button id="btn" className="btn btn-primary btn-block" type="submit">
          Confirm and Generate
        </button><br/>
        {error && <p>{error.message}</p>}	  
      </form>
      <hr />
      <center>
        <div className="row">
          <div id="phonenumber" className="col6">
            Contact No.: 123456
          </div>
          <div className="col6">
            Timing: 10:00am-11:00pm
          </div>
        </div>
      </center>
      <div>  
  </div>
  </div>  //parent div 
    );
  }
}


const PdfPage =withRouter(withFirebase(pdfBase));

export default pdf;

export { PdfPage };
