import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import { withFirebase } from '../Firebase';
import firebase from 'firebase';

import './index.css'
import 'bootstrap/dist/css/bootstrap.min.css';
import * as ROUTES from '../../constants/routes';
import ReactSearchBox from 'react-search-box'

class scheduling extends React.Component {

  async getData(){
    let user = await firebase.firestore().collection('Patient_Details').get()
    .then(querySnapshot => {
      querySnapshot.docs.map(doc => {
        console.log('LOG 1', doc.data());
        let user = doc.data();
        return {
          key:user.name,
          value:user.name,
        };
      });
    });
    return user;
  }

  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      users: [],
    };
    let user = this.getData();

    this.setState({
            users: user,
            loading: false,
    });
    console.log('USERS',this.users);

  }
  
  render() {
    const  users = this.getData();
    return (
      <div>
  <div class="wrap">
   <div class="search">
     <center><h3>Search a patient</h3></center>
   <ReactSearchBox 
      placeholder="Enter patient name"
      data={users}
      onSelect={event =>  window.location.href=ROUTES.SPEECH}
      onFocus={() => {
        console.log('This function is called when is focussed')
      }}

      onChange={name => console.log(name)}
      fuseConfigs={{
        threshold: 0.05,
      }}
      value=" "
    />
</div>
 <br/>
    <h4>Or</h4>
    <br/>
    
    <h3> <Link to={ROUTES.PATIENT_REG}>Register new patient</Link></h3>
  </div>

  <div>
        {loading && <div>Loading ...</div>}
        <UserList users={users} />
      </div>
 
  </div>

);
  }
}

const UserList = ({ users }) => (
  <ul>
    {users.map(user => (
      <li key={user.email}>
        <span>
          <strong>Email:</strong> {user.email}
        </span>
        
        <span>
          <strong>Name:</strong> {user.name}
        </span>
      </li>
    ))}
  </ul>
);

export default withFirebase(scheduling);
