import React, { Component } from 'react';

import { Link, withRouter } from 'react-router-dom';

import { withFirebase } from '../Firebase';


import firebase from 'firebase';

const SchedulePage = () => (
  <div>
    <h1>Schedule (Assistant)</h1>
    <SignUpForm/>
  </div>
  
);

const INITIAL_STATE = {
  username: '',
  id: '',  
  error: null,
};

class AppointmentFormBase extends Component {
  constructor(props) {
    super(props);
	
	this.state = { ...INITIAL_STATE };
  }
  onSubmit = event => {
	  const { username, id } = this.state;
    
      firebase.database().ref('/patients').set(this.state);
    console.log('DATA SAVED');

  }

  onChange = event => {
	  this.setState({ [event.target.name]: event.target.value });
  };
  render() {
	  const {
      username,
      id,
      error,
    } = this.state;
	
	const isInvalid = email === '' || username === '';
	  
    return (
      <form onSubmit={this.onSubmit}>
	  <input
          name="username"
          value={username}
          onChange={this.onChange}
          type="text"
          placeholder="Full Name"
        /><br/>
        <input
          name="id"
          value={id}
          onChange={this.onChange}
          type="text"
          placeholder="Email Address"
        /><br/>        
        <button disabled={isInvalid} type="submit">
          Appoint
        </button>
        {error && <p>{error.message}</p>}	  
      </form>
    );
  }
}

const SignUpForm =withRouter(withFirebase(AppointmentFormBase));

export default SchedulePage;

export { SignUpForm };
